import React, { useState } from "react";
import { GoogleGenerativeAI } from "@google/generative-ai";

function AIDoctor() {
  // Initial history with default messages
  const [history, setHistory] = useState([
    {
      role: "user",
      parts: [{ text: "Hello" }],
    },
    {
      role: "model",
      parts: [{ text: "Great to meet you. What would you like to know?" }],
    },
  ]);

  const [response, setResponse] = useState(""); // To store model response
  const [input, setInput] = useState(""); // To capture user input
  const [customInstruction, setCustomInstruction] = useState(
    "Remember, when I tell you the symptoms, if it is a minor illness, you can give me medicine"
  ); // Save the custom instruction

  // Function to handle sending a new message
  const handleSendMessage = async () => {
    const genAI = new GoogleGenerativeAI(
      "AIzaSyBhtwZ7D1H9w6iAg31HcO2CankgOh5dsHk"
    );
    const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });

    try {
      // Update history by adding custom instruction and user input
      const updatedHistory = [
        {
          role: "system",
          parts: [{ text: customInstruction }], // Add the instruction in each conversation
        },
        ...history,
        {
          role: "user",
          parts: [{ text: input }],
        },
      ];
      setHistory(updatedHistory); // Update local state with new message history

      // Start chat with the updated history
      const chat = model.startChat({ history: updatedHistory });

      // Send the user input and await response
      const result = await chat.sendMessage(input);
      const text = result.response.text(); // Get the model's response

      // Add model response to history
      setHistory((prevHistory) => [
        ...prevHistory,
        {
          role: "model",
          parts: [{ text }],
        },
      ]);

      setInput(""); // Clear the input field after sending the message
      setResponse(text); // Update UI with the model's response
    } catch (error) {
      console.error("Error fetching AI response:", error);
    }
  };

  return (
    <div className="chat-view w-full h-screen mt-10 flex justify-center items-center">
      <div className="chat-body w-full h-full m-10 flex justify-center items-center rounded-3xl shadow-animate relative">
        <div className="chat w-full h-full flex bg-[#202024] text-white rounded-3xl m-10 z-10">
          <div className="w-96 my-10 ml-10 rounded-3xl border-[#ffffff48] p-2 text-center bg-[#161616] border shadow-sm ">
            <div className="text-xl font-semibold">AI Doctor</div>
            <div className="text-gray-500 mb-10">Chat with AI Doctor</div>
            <div className="selection-box">
              <input type="checkbox" className="" name="" id="" />{" "}
              <label>Need Doctor</label>
              <br className="bg-white" />
              <input type="checkbox" className="" name="" id="" />{" "}
              <label>Nearest pharmacy</label>
            </div>
          </div>
          <div className="w-full flex flex-col m-10 rounded-3xl">
            <div className="chat-view flex w-full h-full rounded-t-3xl  relative border-x border-t border-[#ffffff48] overflow-y-auto">
              <div className="ai-chat px-10 pt-10 flex w-full flex-col space-y-4">
                {/* Map through the chat history and display each message */}
                {history.map((message, index) => (
                  <div
                    key={index}
                    className={`${
                      message.role === "user"
                        ? "user-chat self-end bg-gray-700"
                        : "ai-model-chat self-start bg-blue-500"
                    } p-4 max-w-[80%] rounded-lg shadow-lg`}
                  >
                    <div className="text-lg text-white font-semibold">
                      {message.parts[0].text}
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="chat-input flex w-full h-20 rounded-b-3xl border bg-[#303030] border-[#ffffff48]">
              <input
                type="text"
                className="w-full bg-transparent ml-4 border-none outline-none text-white"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message..."
              />
              <button
                onClick={handleSendMessage}
                className="bg-gray-600 hover:bg-blue-500 ml-3 w-20 rounded-br-3xl"
              >
                Send
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AIDoctor;
